package com.oper;

public class OperEx06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a = true;
		boolean b = false;

		System.out.println("a && b : " + (a&&b));
		System.out.println("a || b : " + (a||b));
		System.out.println("!a : " + !a);
		
		int x = 64;
		System.out.println("x << 3 : " + (x << 3));
		System.out.println("x >> 3 : " + (x >> 3));
		
		int y = 3;
		System.out.println(y >> 1);
	}

}
