package com.arrayex;

public class ArrayEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] arr = new int[5];
		char[] ch = new char[4];

		for(int i = 0; i < arr.length; i++) {
			System.out.print(arr[i]);
		}
		System.out.println();
		for(int i = 0; i < ch.length; i++) {
			System.out.print(ch[i] + "\t");
		}
	}

}
