package com.arrayex;

public class ArrayEx03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0; i < args.length; i++) {
			System.out.print(args[i]);
		}
		System.out.println();
		for(int i = 0; i < args.length; i++) {
			System.out.print(i + " ");
		}
	}

}
